return items.map(item => {
  const out = item.json.output;
  let parsed = null;
  let raw = out;

  // Se j√° for objeto, usa direto
  if (typeof out === 'object' && out !== null) {
    parsed = out;
  } else if (typeof out === 'string') {
    // Remove blocos de c√≥digo markdown
    raw = raw.replace(/```(?:json)?\s*/i, '').replace(/```$/i, '').trim();

    // Tenta parse direto
    try {
      parsed = JSON.parse(raw);
    } catch (e) {
      // Se falhar, tenta encontrar o primeiro {...} balanceado
      const match = raw.match(/\{[\s\S]*\}$/m); // pega do primeiro { at√© o fim
      if (match) {
        const candidate = match[0];
        try {
          parsed = JSON.parse(candidate);
        } catch (e2) {
          // √∫ltima tentativa: extrair por par√™nteses balanceados mais complexos
          // n√£o temos parser completo ‚Äî fallback para intencao indefinida
          parsed = null;
        }
      } else {
        parsed = null;
      }
    }
  }

  // Valida√ß√£o b√°sica do schema
  const defaultResponse = {
    id_usuario: item.json?.body?.data?.key?.remoteJid || '',
    intencao: 'indefinida',
    descricao: '',
    dataHora: '',
    status: ''
  };

  if (!parsed || typeof parsed !== 'object' || !parsed.intencao) {
    // retorna esquema padr√£o 'indefinida'
    return { json: { ...item.json, output: defaultResponse } };
  }

  // Normalize fields
  parsed.id_usuario = parsed.id_usuario || (item.json?.body?.data?.key?.remoteJid || '');
  parsed.intencao = String(parsed.intencao).toLowerCase().trim();
  parsed.descricao = parsed.descricao || '';
  parsed.dataHora = parsed.dataHora || '';
  parsed.status = parsed.status || (parsed.intencao === 'criar' ? 'pendente' : (parsed.intencao === 'concluir' ? 'conclu√≠da' : ''));

  return { json: { ...item.json, output: parsed } };
});

