// --- Configura√ß√µes r√°pidas ---
const appendJidSuffix = false; // troque para true se o WAHA precisar de JID (ex: 5511999999999@s.whatsapp.net)
const jidSuffix = '@s.whatsapp.net';

// --- Helpers ---
const rows = items.map(i => i.json).filter(Boolean);

function findUserField(task) {
  const keys = [
    'user','id_usuario','idUsuario','whatsapp_number','whatsappNumber',
    'phone','numero','from','to','remoteJid','jid','remote_jid'
  ];
  for (const k of keys) {
    if (task[k]) return task[k];
  }
  if (task.body?.data?.key?.remoteJid) return task.body.data.key.remoteJid;
  if (task.message?.key?.remoteJid) return task.message.key.remoteJid;
  if (task.message?.from) return task.message.from;
  return null;
}

function normalizeNumber(raw) {
  if (!raw && raw !== 0) return null;
  let s = String(raw).trim();
  if (s.includes('@')) return s;
  const digits = s.replace(/\D/g, '');
  return digits || s;
}

function getDescription(t) {
  return t.task || t.descricao || t.title || t.description || t.name || t.note || '';
}
function getDate(t) {
  return t.dataHora || t.data || t.datetime || t.date || t.when || '';
}

// --- Varia√ß√µes de mensagens para n√£o parecer autom√°tica ---
const greetings = [
