const output = [];
for (const [uid, tasks] of Object.entries(groups)) {
  const whatsapp_number = uid;
  const whatsapp_jid = (appendJidSuffix && !String(uid).includes('@')) ? `${uid}${jidSuffix}` : uid;

  // Escolhe aleatoriamente uma sauda√ß√£o e um fechamento
  const greeting = greetings[Math.floor(Math.random() * greetings.length)];
